mapper.select:
SELECT B.TBNO AS TBNO, ID, BPRICE, substring(BTITLE,1,8) AS BTITLE, HITS, LIKES, D1.CINFO AS BRDNAME, D2.CINFO AS KINDNAME, D3.CINFO AS CATE1NAME, D4.CINFO AS CATE2NAME, D5.CINFO AS CATE3NAME, D7.CINFO AS BSTATUSNAME, T1.PATH AS PATH, CDATE, UDATE, IFNULL(COMMCNT,0) AS COMMCNT, DELYN FROM T3_TBRD B INNER JOIN T3_DESC D1 ON B.BRDFLG = D1.CNUM INNER JOIN T3_DESC D2 ON B.KIND = D2.CNUM INNER JOIN T3_DESC D3 ON B.CATE1 = D3.CNUM INNER JOIN T3_DESC D4 ON B.CATE2 = D4.CNUM INNER JOIN T3_DESC D5 ON B.CATE3 = D5.CNUM INNER JOIN T3_DESC D7 ON B.BSTATUS = D7.CNUM LEFT JOIN (SELECT TBNO,PATH,REP FROM T3_TIMG WHERE REP='Y') T1 ON B.TBNO = T1.TBNO LEFT JOIN (SELECT TBNO,COUNT(CNO) AS COMMCNT FROM T3_CMRE GROUP BY TBNO) C1 ON B.TBNO = C1.TBNO WHERE DELYN="N"
AND BRDFLG="BF1"
AND BRDFLG=#{sbrdflg}
AND CATE1=#{cate1} AND CATE2=#{cate2} AND CATE3=#{cate3} ORDER BY TBNO DESC LIMIT #{startNum}, #{lastNum}
AND CATE1=#{cate1} AND CATE2=#{cate2} ORDER BY TBNO DESC LIMIT #{startNum}, #{lastNum}
AND CATE1=#{cate1} ORDER BY TBNO DESC LIMIT #{startNum}, #{lastNum}
ORDER BY TBNO DESC LIMIT #{startNum}, #{lastNum}

SELECT
	COUNT(TBNO) AS TBRDCNT
FROM T3_TBRD
WHERE DELYN="N" AND BRDFLG=#{sbrdflg}

	
		AND CATE1=#{cate1} AND CATE2=#{cate2} AND CATE3=#{cate3}
	
	
		AND CATE1=#{cate1} AND CATE2=#{cate2}
	
	
		AND CATE1=#{cate1}

SELECT 
	DISTINCT(si) AS si
FROM T_AREA;

SELECT NICK
FROM T3_USER
WHERE ID=#{id}

mapper.update:
UPDATE T3_TBRD
	SET HITS=HITS+1
WHERE TBNO=#{tbno};

UPDATE T3_TBRD
	
	
			SET BSTATUS=#{bstatus},
			TRADE=NULL
		WHERE TBNO= #{tbno}
	
	
	SET BSTATUS=#{bstatus}
	WHERE TBNO= #{tbno}

UPDATE T3_TBRD
	SET LIKES=LIKES+1
WHERE TBNO=#{tbno}

mapper.select:
SELECT COUNT(JJNO) AS JIMCNT
FROM T3_JJIM 
WHERE TBNO=#{tbno}

SELECT COUNT(JJNO) AS JIMCNT
FROM T3_JJIM 
WHERE ID=#{id} AND TBNO=#{tbno}

mapper.delete:
DELETE 
FROM T3_JJIM 
WHERE ID=#{id} AND TBNO=#{tbno}

mapper.update:
UPDATE T3_TBRD
	SET LIKES=LIKES-1
WHERE TBNO=#{tbno}

UPDATE T3_TBRD
	SET DELYN="Y"
WHERE TBNO= #{tbno}

mapper.select:
SELECT COUNT(CNO) AS CNT
FROM T3_CMRE 
WHERE TBNO=#{tbno};

mapper.update:
UPDATE T3_CMRE
SET CONTE=#{conetent}
WHERE CNO=#{cno} AND ID=#{id};

UPDATE T3_CMRE
SET DELYN="Y"
WHERE CNO=#{cno}

mapper.select:
SELECT COUNT(CNO)
FROM T3_CMRE
WHERE TBNO=#{tbno} and REVWFLG=1;
