mapper.select:
SELECT B.CBNO, B.ID AS ID, CATE1, CATE2, CTITLE, CCONT, HITS, B.CDATE, B.UDATE, DELYN, D1.CINFO AS CATE1NAME, D2.CINFO AS CATE2NAME, U1.NICK AS NICK, IFNULL(COMMCNT,0) AS COMMCNT FROM T3_CBRD B INNER JOIN T3_DESC D1 ON B.CATE1 = D1.CNUM INNER JOIN T3_DESC D2 ON B.CATE2 = D2.CNUM INNER JOIN T3_USER U1 ON B.ID = U1.ID LEFT JOIN (SELECT CBNO ,COUNT(CNO) AS COMMCNT FROM T3_CMRE GROUP BY CBNO) C1 ON B.CBNO = C1.CBNO
AND D1.CNUM = #{cate1} ORDER BY CBNO DESC LIMIT #{startNum}, #{lastNum}
AND D1.CNUM = #{cate1} AND D2.CNUM = #{cate2} ORDER BY CBNO DESC LIMIT #{startNum}, #{lastNum}
ORDER BY CBNO DESC LIMIT #{startNum}, #{lastNum}

SELECT COUNT(CBNO) AS CBRDCNT
FROM T3_CBRD B
INNER JOIN T3_DESC D1 ON B.CATE1 = D1.CNUM
INNER JOIN T3_DESC D2 ON B.CATE2 = D2.CNUM

	
		AND D1.CNUM = #{cate1}
	
	
		AND D1.CNUM = #{cate1} AND D2.CNUM = #{cate2}

SELECT COUNT(CNO) AS CCNT
FROM T3_CMRE
WHERE CBNO = #{cbno};
